package Basicprograms;

public class SumandAverageOfNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n1=70,n2=85,n3=80,n4=90,n5=75;
		int sum;
		sum=n1+n2+n3+n4+n5;
		System.out.println(sum);
		
		int avg;
		avg=sum/5;
		System.out.println(avg);

	}

}
